<?phpinclude "../Clases/TemplatePage.php";include "../Clases/Fuec.php";$util = new Utilities();$template = new TemplatePage(true, true, 'empleado');if(!isset($_SESSION['number'])){	header('location:ContractExtract_slct.php');}$template->headerForms('FUEC');$template->navigateBar('FuecPassenger_add');$util->result = $util->db->Execute("SELECT V.CC_CAPACIDAD_FLD AS size FROM CC_FUEC_TBL F INNER JOIN CC_VEHICLE_TBL V ON F.CC_PLACA_FLD = V.CC_PLACA_FLD WHERE F.CC_NUMERO_FUEC_FLD = '" . $_SESSION['number'] . "'");$result = $util->result->FetchRow();$fuecForm = new JFormer('fuecForm', array('title' => '<div align="center"><h2>Ocupantes del FUEC No. '.$_SESSION['number'].'</h2></div>', 'submitButtonText' => 'Aceptar', 'requiredText' => '*', 'pageNavigator' => false));$jFormPage1 = new JFormPage($fuecForm->id . 'BasicData');$jFormSection1 = new JFormSection($fuecForm->id . 'Basic', array(        'title' => '<p>Pasajero</p>',        'instanceOptions' => array('max' => (int)$result['size'], 'addButtonText' => 'Adicionar', 'removeButtonText' => 'Quitar',)));$jFormSection1->addJFormComponentArray(array(	new JFormComponentDropDown('docType', 'Tipo de documento:  ', $util->fillDropDown('cc_tipo_doc_fld'), array('disabled' => false, 'validationOptions' => array('required'))),    new JFormComponentSingleLineText('docNum', 'Documento:  ', array('initialValue' => '', 'disabled' => false, 'validationOptions' => array('required'))),    new JFormComponentSingleLineText('name', 'Nombre:  ', array('initialValue' => '', 'disabled' => false, 'validationOptions' => array('required'))),    ));$jFormPage1->addJFormSection($jFormSection1);$fuecForm->addJFormPage($jFormPage1);function onSubmit($formValues) {	$fuec = new Fuec();	$respCode = $fuec->addFuecPassenger($formValues, $_SESSION['number']);	if($respCode != 0) {		switch ($respCode) {			default:				$response = array('failureNoticeHtml' => 'Inconvenientes tecnicos al procesar los ocupantes.');		}	}	else {		$response = array('successPageHtml' => '<meta http-equiv="refresh" content="0; url=Fuec_srch.php">');	}	return $response;}$template->bodyForms($fuecForm);$template->tail();?>