<?phpinclude "../Clases/TemplatePage.php";include "../Clases/Fuec.php";$util = new Utilities();$template = new TemplatePage(true, true, 'empleado');if(!isset($_SESSION['number'])){	header('location:ContractExtract_slct.php');}$template->headerForms('FUEC');$template->navigateBar('FuecPassenger_add');$fuec = new Fuec();$fuec->getPassengersFuec($_SESSION['number']);$fuecForm = new JFormer('fuecForm', array('title' => '<div align="center"><h2>Ocupantes del FUEC No. '.$_SESSION['number'].'</h2></div>', 'submitButtonText' => 'Aceptar', 'requiredText' => '*', 'pageNavigator' => false));$jFormPage1 = new JFormPage($fuecForm->id . 'BasicData');$i = 1;while($passenger = $fuec->result->FetchRow()) {	$jFormSection = new JFormSection($fuecForm->id . 'Basic' . $i, array('title' => '<p>Pasajero ' . $i . '</p>'));	$jFormSection->addJFormComponentArray(array(		new JFormComponentDropDown('docType' . $i, 'Tipo de documento:  ', $util->fillDropDownVew('cc_tipo_doc_fld', $passenger['docType']), array('disabled' => false, 'validationOptions' => array('required'))),		new JFormComponentSingleLineText('docNum' . $i, 'Documento:  ', array('initialValue' => $passenger['docNum'], 'disabled' => false, 'validationOptions' => array('required'))),		new JFormComponentSingleLineText('name' . $i, 'Nombre:  ', array('initialValue' => $passenger['name'], 'disabled' => false, 'validationOptions' => array('required'))),    	));	$jFormPage1->addJFormSection($jFormSection);	$i++;}$fuecForm->addJFormPage($jFormPage1);function onSubmit($formValues) {	$fuec = new Fuec();	$respCode = $fuec->modifyFuecPassenger($formValues, $_SESSION['number']);	if($respCode != 0) {		switch ($respCode) {			case 1:				$response = array('failureNoticeHtml' => 'No hay capacidad para ingresar mas ocupantes.');				break;			default:				$response = array('failureNoticeHtml' => 'Inconvenientes tecnicos al procesar los ocupantes.');		}	}	else {		$response = array('successPageHtml' => '<meta http-equiv="refresh" content="0; url=Fuec_srch.php">');	}	return $response;}$template->bodyForms($fuecForm);$template->tail();?>