<?phpinclude "../Clases/TemplatePage.php";include "../Clases/Fuec.php";$util = new Utilities();$template = new TemplatePage(true, true, 'empleado');$template->headerForms('FUEC');$template->navigateBar('Fuec_add');$fuecForm = new JFormer('fuecForm', array('title' => '<div align="center"><h2>FUEC No. 376009200' . date('Y') . '########</h2></div>', 'submitButtonText' => 'Aceptar', 'requiredText' => '*', 'pageNavigator' => false));$jFormPage1 = new JFormPage($fuecForm->id . 'BasicData', array('title' => 'Basicos'));$jFormSection1 = new JFormSection($fuecForm->id . 'Basic');$jFormSection1->addJFormComponentArray(array(    new JFormComponentSingleLineText('contractNumber', 'Numero Contrato:  ', array('initialValue' => '', 'disabled' => false)),    new JFormComponentSingleLineText('plate', 'Placa:  ', array('initialValue' => '', 'disabled' => false)),));$jFormSection2 = new JFormSection($fuecForm->id . 'Driver1', array('title' => '<p>Conductor 1</p>'));$jFormSection2->addJFormComponentArray(array(	new JFormComponentDropDown('docTypeDriver1', 'Tipo de documento:  ', $util->fillDropDown('cc_tipo_doc_fld'), array('disabled' => false,)),    new JFormComponentSingleLineText('docNumDriver1', 'Documento:  ', array('initialValue' => '', 'disabled' => false,)),));$jFormSection2_1 = new JFormSection($fuecForm->id . 'Driver2', array('title' => '<p>Conductor 2</p>'));$jFormSection2_1->addJFormComponentArray(array(	new JFormComponentDropDown('docTypeDriver2', 'Tipo de documento:  ', $util->fillDropDown('cc_tipo_doc_fld'), array('disabled' => false,)),    new JFormComponentSingleLineText('docNumDriver2', 'Documento:  ', array('initialValue' => '', 'disabled' => false,)),));$jFormPage1->addJFormSection($jFormSection1);$jFormPage1->addJFormSection($jFormSection2);$jFormPage1->addJFormSection($jFormSection2_1);$fuecForm->addJFormPage($jFormPage1);function onSubmit($formValues) {	$fuec = new Fuec();	$respCode = $fuec->addFuec($formValues, $_SESSION['numFuec'], $_SESSION['number']);	if($respCode != 0) {		switch ($respCode) {			case 1:				$response = array('failureNoticeHtml' => 'Inconvenientes tecnicos al procesar el FUEC.');				break;			case 2:				$response = array('failureNoticeHtml' => 'Inconvenientes tecnicos al procesar los ocupantes.');				break;		}	}	else {		$response = array('successPageHtml' => '<meta http-equiv="refresh" content="0; url=Fuec_srch.php">');	}	return $response;}$template->bodyForms($fuecForm);$template->tail();?>