<?phpinclude "../Clases/TemplatePage.php";include "../Clases/Fuec.php";$util = new Utilities();$template = new TemplatePage(true, true, 'empleado');if(!isset($_SESSION['number'])){	header('location:ContractExtract_slct.php');}$fuec = new Fuec();$fuec->getNextConsecutive();$result = $fuec->result->FetchRow();$_SESSION['numFuec'] = '376009200' . date('Y') . $_SESSION['number'] . $result['number'];$template->headerForms('FUEC');$template->navigateBar('Fuec_add');$fuecForm = new JFormer('fuecForm', array('title' => '<div align="center"><h2>FUEC No. '.$_SESSION['numFuec'].'</h2></div>', 'submitButtonText' => 'Aceptar', 'requiredText' => '*', 'pageNavigator' => false));$jFormPage1 = new JFormPage($fuecForm->id . 'BasicData', array('title' => 'Basicos'));$jFormSection1 = new JFormSection($fuecForm->id . 'Basic', array(        'title' => '<p>Pasajero</p>',        'instanceOptions' => array('max' => 50, 'addButtonText' => 'Adicionar', 'removeButtonText' => 'Quitar',)));$jFormSection1->addJFormComponentArray(array(    new JFormComponentSingleLineText('docNum', 'Documento:  ', array('initialValue' => '', 'disabled' => false)),    new JFormComponentSingleLineText('name', 'Nombre:  ', array('initialValue' => '', 'disabled' => false)),    ));$jFormPage1->addJFormSection($jFormSection1);$fuecForm->addJFormPage($jFormPage1);function onSubmit($formValues) {	$fuec = new Fuec();	$respCode = $fuec->addFuec($formValues, $_SESSION['numFuec'], $_SESSION['number']);	if($respCode != 0) {		switch ($respCode) {			case 1:				$response = array('failureNoticeHtml' => 'Inconvenientes tecnicos al procesar el FUEC.');				break;			case 2:				$response = array('failureNoticeHtml' => 'Inconvenientes tecnicos al procesar los ocupantes.');				break;		}	}	else {		$response = array('successPageHtml' => '<meta http-equiv="refresh" content="0; url=Fuec_srch.php">');	}	return $response;}$template->bodyForms($fuecForm);$template->tail();?>