<?phpinclude "../Clases/TemplatePage.php";include "../Clases/Fuec.php";$util = new Utilities();$template = new TemplatePage(true, true, 'empleado');if(!isset($_SESSION['number'])){	header('location:ContractExtract_slct.php');}$fuec = new Fuec();$fuec->getNextConsecutive();$result = $fuec->result->FetchRow();$_SESSION['numFuec'] = '376009200' . date('Y') . $_SESSION['number'] . $result['number'];$template->headerForms('FUEC');$template->navigateBar('Fuec_add');$fuecForm = new JFormer('fuecForm', array('title' => '<div align="center"><h2>FUEC No. '.$_SESSION['numFuec'].'</h2></div>', 'submitButtonText' => 'Aceptar', 'requiredText' => '*', 'pageNavigator' => true));$jFormPage1 = new JFormPage($fuecForm->id . 'BasicData', array('title' => 'Ocupantes'));$jFormSection1 = new JFormSection($fuecForm->id . 'Basic', array(        'title' => '<p>Pasajero</p>',        'instanceOptions' => array('max' => 50, 'addButtonText' => 'Adicionar', 'removeButtonText' => 'Quitar',)));$jFormSection1->addJFormComponentArray(array(	new JFormComponentDropDown('docType', 'Tipo de documento:  ', $util->fillDropDown('cc_tipo_doc_fld'), array('disabled' => false,)),    new JFormComponentSingleLineText('docNum', 'Documento:  ', array('initialValue' => '', 'disabled' => false,)),    new JFormComponentSingleLineText('name', 'Nombre:  ', array('initialValue' => '', 'disabled' => false,)),    ));$jFormPage1->addJFormSection($jFormSection1);$fuecForm->addJFormPage($jFormPage1);$jFormPage2 = new JFormPage($fuecForm->id . 'VehicleData', array('title' => 'Vehiculo'));$jFormSection2 = new JFormSection($fuecForm->id . 'Vehicle');$jFormSection2->addJFormComponentArray(array(    new JFormComponentSingleLineText('plate', 'Placa:  ', array('initialValue' => '', 'disabled' => false,)),));$jFormPage2->addJFormSection($jFormSection2);$fuecForm->addJFormPage($jFormPage2);$jFormPage3 = new JFormPage($fuecForm->id . 'Driver1Data', array('title' => 'Conductor 1'));$jFormSection3 = new JFormSection($fuecForm->id . 'Driver1', array('title' => '<p>Conductor</p>'));$jFormSection3->addJFormComponentArray(array(	new JFormComponentDropDown('docTypeDriver', 'Tipo de documento:  ', $util->fillDropDown('cc_tipo_doc_fld'), array('disabled' => false,)),    new JFormComponentSingleLineText('docNumDriver', 'Documento:  ', array('initialValue' => '', 'disabled' => false,)),));$jFormPage3->addJFormSection($jFormSection3);$fuecForm->addJFormPage($jFormPage3);$jFormPage4 = new JFormPage($fuecForm->id . 'Driver2Data', array('title' => 'Conductor 2'));$jFormSection4 = new JFormSection($fuecForm->id . 'Driver2', array('title' => '<p>Conductor</p>'));$jFormSection4->addJFormComponentArray(array(	new JFormComponentDropDown('docTypeDriver', 'Tipo de documento:  ', $util->fillDropDown('cc_tipo_doc_fld'), array('disabled' => false,)),    new JFormComponentSingleLineText('docNumDriver', 'Documento:  ', array('initialValue' => '', 'disabled' => false,)),));$jFormPage4->addJFormSection($jFormSection4);$fuecForm->addJFormPage($jFormPage4);function onSubmit($formValues) {	$fuec = new Fuec();	$respCode = $fuec->addFuec($formValues, $_SESSION['numFuec'], $_SESSION['number']);	if($respCode != 0) {		switch ($respCode) {			case 1:				$response = array('failureNoticeHtml' => 'Inconvenientes tecnicos al procesar el FUEC.');				break;			case 2:				$response = array('failureNoticeHtml' => 'Inconvenientes tecnicos al procesar los ocupantes.');				break;		}	}	else {		$response = array('successPageHtml' => '<meta http-equiv="refresh" content="0; url=Fuec_srch.php">');	}	return $response;}$template->bodyForms($fuecForm);$template->tail();?>